kahuna.apiversions = {

	ApiVersionsCtrl: function($rootScope, $scope, $http, $resource, $routeParams, $location, KahunaData) {

		$rootScope.currentPage = 'apiversions';
		$rootScope.currentPageHelp = 'docs/rest-apis/urls#TOC-API-version-Request';
		$rootScope.helpDialog('apiversions', 'Help', localStorage['eslo-ld-learn-complete']);

		// Fetch all API versions for the current project
		kahuna.meta.getAllApiVersions($rootScope.currentProject, function (data) {
			$scope.selectedApiVersion = null;
			$scope.apiVersions = data;
			if (data.length == 0)
				return;
			kahuna.applyFunctionInScope($scope, function() {
				$scope.selectedApiVersion = data[data.length - 1];
			});
		});

		var findApiVersionIndex = function(version) {
			if ( ! $scope.apiVersions)
				return -1;
			for (var idx = 0; idx < $scope.apiVersions.length; idx++) {
				if ($scope.apiVersions[idx].ident == version.ident)
					return idx;
			}
		};

		// Update the currently selected API version
		$scope.saveApiVersion = function() {
			KahunaData.update($scope.selectedApiVersion, function(data) {
				for (var i = 0; i < data.txsummary.length; i++) {
					var modObj = data.txsummary[i];
					if (modObj['@metadata'].resource === 'admin:apiversions' && modObj.ident === $scope.selectedApiVersion.ident) {
						var updatedIndex = findApiVersionIndex(modObj);
						$scope.apiVersions[updatedIndex] = modObj;
						$scope.selectedApiVersion = modObj;
					}
				}
				kahuna.meta.getAllApiVersions($rootScope.currentProject, function () {
					kahuna.applyFunctionInScope($scope, function () {
						kahuna.setLiveBrowserUrl($rootScope, $scope.currentProject);
					});
				});
				kahuna.util.info('API version was saved');
			});
		};

		// Create a new API version
		$scope.createApiVersion = function() {
			// Look for available name, starting with v1 and going up
			var versionName = 'v1';
			if ($scope.apiVersions) {
				for (var versionNumber = 1; versionNumber < 10000; versionNumber++) {
					var tentativeName = 'v' + versionNumber;
					if ($scope.apiVersions.some(function(version) { return version.name == tentativeName; })) {
						continue;
					}
					versionName = 'v' + versionNumber;
					break;
				}
			}
			var newObj = {
					"project_ident": $scope.currentProject.ident,
					"name": versionName
				};
			KahunaData.create('admin:apiversions', newObj, function (data) {
				for (var i = 0; i < data.txsummary.length; i++) {
					var modObj = data.txsummary[i];
					if (modObj['@metadata'].resource === 'admin:apiversions' && modObj['@metadata'].verb === 'INSERT') {
						$scope.apiVersions.push(modObj);
						$scope.selectedApiVersion = modObj;

					}
				}
				kahuna.meta.getAllApiVersions($rootScope.currentProject, function () {
					kahuna.applyFunctionInScope($scope, function () {
						kahuna.setLiveBrowserUrl($rootScope, $scope.currentProject);
					});
				});
			});
		};

		$scope.deleteApiVersion = function() {
			if ( ! confirm("Are you sure you want to delete this API version (" + $scope.selectedApiVersion.name +
					")? This will also delete all the resources it contains."))
				return;

			// If this is a brand-new, not-yet-saved object
			if ( ! $scope.selectedApiVersion['@metadata']) {

				// Remove from list
				var idx = $scope.apiVersions.indexOf($scope.selectedApiVersion);
				if (idx > -1) {
					$scope.apiVersions.splice(idx, 1);

					// And select whatever API version is left
					if ($scope.apiVersions.length > 0)
						$scope.selectedApiVersion = $scope.apiVersions[0];
					else
						$scope.selectedApiVersion = null;
				}
				return;
			}

			KahunaData.remove($scope.selectedApiVersion, function(data){
				for (var i = 0; i < data.txsummary.length; i++) {
					var modObj = data.txsummary[i];
					if (modObj['@metadata'].resource === 'admin:apiversions' && modObj.ident === $scope.selectedApiVersion.ident) {
						var deletedIndex = findApiVersionIndex(modObj);
						$scope.apiVersions.splice(deletedIndex, 1);
						if ($scope.apiVersions.length > 0) {
							$scope.selectedApiVersion = $scope.apiVersions[0];
						}
						else {
							$scope.selectedApiVersion = null;
						}
					}
				}
				kahuna.meta.getAllApiVersions($rootScope.currentProject, function () {
					kahuna.applyFunctionInScope($scope, function () {
						kahuna.setLiveBrowserUrl($rootScope, $scope.currentProject);
					});
				});
				kahuna.util.info('API version was deleted');
			});
		};
	}
};

kahuna.apikey = {

	ApiKeyCtrl : function($rootScope, $scope, $http, $resource, $routeParams, $location, KahunaData) {

//		$( "#EditorTabs" ).tabs();
		$rootScope.currentPage = 'apikeys';
		$rootScope.currentPageHelp = 'docs/logic-designer/security/authentication';
		$rootScope.helpDialog('apikey', 'Help', localStorage['eslo-ld-learn-complete']);

		$scope.fetchApiKeys = function() {
			// Fetch all API keys for the current project
			var autoQualif = "";
			if ( ! $scope.showAutoKeys)
				autoQualif = " AND origin is null";
			var searchStr = "";
			if ($scope.apiKeySearch)
				searchStr = " AND UPPER(name) like '%" + $scope.apiKeySearch.toUpperCase() + "%'";
			KahunaData.query('AllApiKeys', {pagesize: 10000, filter: 'project_ident=' + $scope.currentProject.ident
					+ autoQualif + searchStr}, 
				function(data){
				$scope.apiKeys = data;
				if (data.length == 0)
					return;
				$scope.selectedApiKeys = [data[0]];

				// Grab all the roles, we'll need them later
				KahunaData.query('admin:roles', 'project_ident=' + $scope.currentProject.ident, function (rolesData) {
					$scope.roles = {};
					rolesData.forEach(function(role){
						role.checked = false;
						$scope.roles[role.ident] = role;
					});
					$scope.apiKeySelected();
				});
			});
		};

		$scope.fetchApiKeys();

//		$('#apiKeyExpiration').datetimepicker({
//			dateFormat: "yy-mm-dd",
//			timeFormat: "HH:mm",
//			hourGrid: 6,
//			minuteGrid: 15,
//			separator: ' ',
//			minDate: new Date(),
//			showOn: "focus",
////			buttonImage: "images/calendar.gif",
////			buttonImageOnly: true,
//			onClose: function(d) {
//				$scope.selectedApiKey.expiration = d;
//			}
//		});

//		var nowTemp = new Date();
//		var now = new Date(nowTemp.getFullYear(), nowTemp.getMonth(), nowTemp.getDate(), 0, 0, 0, 0);
		//var datepick = 
		$('#apiKeyExpiration').datepicker({
//			onRender: function(date) {
//				return date.valueOf() < now.valueOf() ? 'disabled' : '';
//			}
//		}).on('changeDate', function(ev) {
//			datepick.hide();
		});

		var findApiKeyIndex = function(apiKey) {
			if ( ! $scope.apiKeys)
				return -1;
			for (var idx = 0; idx < $scope.apiKeys.length; idx++) {
				if ($scope.apiKeys[idx].ident == apiKey.ident)
					return idx;
			}
		};

		$scope.loggers = {
				admini: {name: "Administration", level: "FINE"},
				buslog: {name: "Business logic extensions", level: "FINE"},
				depend: {name: "Dependency analysis", level: "FINE"},
				generl: {name: "General", level: "FINE"},
				persis: {name: "Database access", level: "FINE"},
				engine: {name: "Rules engine", level: "FINE"},
				resrcs: {name: "Resources", level: "FINE"},
				securi: {name: "Security", level: "FINE"},
				sysdbg: {name: "System debug", level: "FINE"},
				ulogic: {name: "User logic", level: "FINE"}
		};

		$scope.$watch("showAutoKeys", $scope.fetchApiKeys);

		$scope.$watch("apiKeySearch", $scope.fetchApiKeys);

		$scope.apiKeySelected = function() {
			// Set up the roles
			for (var roleIdent in $scope.roles) {
				$scope.roles[roleIdent].checked = false;
			}
			if ($scope.selectedApiKeys.length != 1) {
				$scope.selectedApiKey = null;
				return;
			}
			$scope.selectedApiKey = $scope.selectedApiKeys[0];
			for (var i = 0; i < $scope.selectedApiKeys[0].ApiKeyRoles.length; i++) {
				var keyRole = $scope.selectedApiKeys[0].ApiKeyRoles[i];
				if (keyRole['@metadata'].action == 'DELETE')
					continue;
				$scope.roles[keyRole.role_ident].checked = true;
			}

			// Set up logging info
			if ($scope.selectedApiKey.logging) {
				var logOptions = $scope.selectedApiKeys[0].logging.split(',');
				for (var i = 0; i < logOptions.length; i++) {
					var optionParts = logOptions[i].split("=");
					if (optionParts[0] == "*") {
						for (var logName in $scope.loggers) {
							$scope.loggers[logName].level = optionParts[1];
						}
					}
					else {
						if ($scope.loggers[optionParts[0]]) // Avoid tripping on old, invalid logger names
							$scope.loggers[optionParts[0]].level = optionParts[1];
					}
				}
			}
			else {
				for (var logName in $scope.loggers)
					$scope.loggers[logName].level = 'OFF';
			}
		};

		$scope.createApiKey = function() {
			var newApiKeyName = 'New API key';
			if ($scope.apiKeys) {
				for (var i = 1; i < 10000; i++) {
					var tentativeName = 'New API key ' + i;
					if ($scope.apiKeys.some(function (apiKey) { return apiKey.name == tentativeName; })) {
						continue;
					}
					newApiKeyName = 'New API key ' + i;
					break;
				}
			}

			var newApiKey = {
					project_ident: $scope.currentProject.ident,
					name: newApiKeyName,
					apikey: "?", // ? will cause a random key to be generated
					status: "A",
					logging: "*=FINE"
				};
			KahunaData.create("AllApiKeys", newApiKey, function(data){
				for (var i = 0; i < data.txsummary.length; i++) {
					var modObj = data.txsummary[i];
					if (modObj['@metadata'].resource === 'AllApiKeys' && modObj['@metadata'].verb === 'INSERT') {
						modObj.ApiKeyRoles = [];
						$scope.apiKeys.push(modObj);
						$scope.selectedApiKey = modObj;
						$scope.selectedApiKeys = [modObj];
					}
				}
			});
		};

		$scope.deleteApiKey = function() {
			if ($scope.selectedApiKeys.length > 1) {
				if ( ! confirm("Are you sure you want to delete these " + $scope.selectedApiKeys.length + " API keys?"))
					return;
			}
			else {
				if ( ! confirm("Are you sure you want to delete this API key (" + $scope.selectedApiKey.name +
							")?"))
					return;
			}

			var numToDelete = $scope.selectedApiKeys.length;
			var originalNumToDelete = $scope.selectedApiKeys.length;
			for (var i = 0; i < $scope.selectedApiKeys.length; i++) {
				KahunaData.remove($scope.selectedApiKeys[i], function (data) {
					for (var i = 0; i < data.txsummary.length; i++) {
						var modObj = data.txsummary[i];
						if (modObj["@metadata"].resource == "AllApiKeys") {
							var deletedIndex = findApiKeyIndex(modObj);
							$scope.apiKeys.splice(deletedIndex, 1);
							if ($scope.apiKeys.length > 0) {
								$scope.selectedApiKey = $scope.apiKeys[0];
								$scope.selectedApiKeys[0] = $scope.apiKeys[0];
							}
							else {
								$scope.selectedApiKey = null;
								$scope.selectedApiKeys = [];
							}
						}
					}
					numToDelete--;
					if (numToDelete == 0) {
						$scope.fetchApiKeys();
						if (originalNumToDelete <= 1)
							kahuna.util.info('API key was deleted');
						else
							kahuna.util.info('' + originalNumToDelete + ' API keys were deleted');
					}
				});
			}
		};

		$scope.saveApiKey = function () {
			if ($scope.selectedApiKeys.length != 1) {
				alert('Please select one API key');
				return;
			}
			KahunaData.update($scope.selectedApiKeys[0], function (data) {
				for (var i = 0; i < data.txsummary.length; i++) {
					var modObj = data.txsummary[i];
					if (modObj["@metadata"].resource == "AllApiKeys" && modObj.ident == $scope.selectedApiKeys[0].ident) {
						var updatedIndex = findApiKeyIndex(modObj);
						modObj.ApiKeyRoles = $scope.apiKeys[updatedIndex].ApiKeyRoles;
						$scope.apiKeys[updatedIndex] = modObj;
						$scope.selectedApiKeys[0] = modObj;
					}
					else if (modObj["@metadata"].resource == "AllApiKeys.ApiKeyRoles" && modObj["@metadata"].verb == 'INSERT') {
						for (var i = 0; i < $scope.selectedApiKeys[0].ApiKeyRoles.length; i++) {
							if ($scope.selectedApiKeys[0].ApiKeyRoles[i].ident == modObj.ident) {
								$scope.selectedApiKeys[0].ApiKeyRoles[i]['@metadata'].checksum = modObj.checksum;
								break;
							}
						}
					}
					else if (modObj["@metadata"].resource == "AllApiKeys.ApiKeyRoles" && modObj["@metadata"].verb == 'DELETE') {
						var delIdx = -1;
						for (var i = 0; i < $scope.selectedApiKeys[0].ApiKeyRoles.length; i++) {
							if ($scope.selectedApiKeys[0].ApiKeyRoles[i].ident == modObj.ident) {
								delIdx = i;
								break;
							}
						}
						$scope.selectedApiKeys[0].ApiKeyRoles.splice(delIdx, 1);
					}
				}
				kahuna.util.info('API key was saved');
			});
		};

		$scope.saveLogging = function () {
			var logVals = [];
			for (var logName in $scope.loggers)
				logVals.push(logName + '=' + $scope.loggers[logName].level);
			$scope.selectedApiKeys[0].logging = logVals.join(',');
			$scope.saveApiKey();
		};

		$scope.roleCheckClicked = function (role) {
			var roleIdent = role.ident;
			var keyRole = null;
			for (var i = 0; i < $scope.selectedApiKeys[0].ApiKeyRoles.length; i++) {
				var kr = $scope.selectedApiKeys[0].ApiKeyRoles[i];
				if (kr.role_ident != roleIdent)
					continue;
				keyRole = kr;
				break;
			}
			if (keyRole) {
				if (role.checked) {
					if (keyRole['@metadata'].action == 'DELETE')
						delete keyRole['@metadata'].action;
				}
				else {
					if (keyRole['@metadata'].action == 'INSERT') {
						var idx = $scope.selectedApiKeys[0].ApiKeyRoles.indexOf(keyRole);
						$scope.selectedApiKeys[0].ApiKeyRoles.splice(idx, 1);
					}
					else if ( ! keyRole['@metadata'].action) {
						keyRole['@metadata'].action = 'DELETE';
					}
				}
			}
			else {
				if (role.checked) {
					keyRole = {
						'@metadata': {action: 'INSERT'},
						apikey_ident: $scope.selectedApiKey.ident,
						role_ident: role.ident
					};
					$scope.selectedApiKeys[0].ApiKeyRoles.push(keyRole);
				}
			}
		};
	}
};

kahuna.apidoc = {

	ApiDocCtrl : function ($rootScope, $scope, $http, $resource, $routeParams, $location, KahunaData) {

		$rootScope.currentPage = 'apidocs';
		$rootScope.currentPageHelp = 'docs/logic-designer/security/authentication';
		$rootScope.helpDialog('apidocs', 'Help', localStorage['eslo-ld-learn-complete']);

		$scope.data = {};
		$scope.data.serverUrl = kahuna.serverUrl;

		var ssIdx = kahuna.serverUrl.indexOf("//");
		var sIdx = kahuna.serverUrl.indexOf("/", ssIdx + 2);
		$scope.data.hostname = kahuna.serverUrl.substring(ssIdx + 2, sIdx);

		$scope.data.allTables = kahuna.meta.allTables;
		$scope.data.tableDetails = {};
		$scope.data.tableNames = [];
		$scope.data.tableAccordion = [];
		$scope.data.resourceDetails = {};

		// Gets called when a table accordion is open
		function showTableDetails(idx) {
			var tblName = $scope.data.tableNames[idx];
			console.log("Show details for table " + tblName);
			if ($scope.data.tableDetails[tblName])
				return;
			KahunaData.query('@tables/' + tblName, {projectId: $scope.currentProject.ident}, function (data) {

				var dtl = "<table>";
				for (var i = 0; i < data.columns.length; i++) {
					dtl += "<tr><td>" + data.columns[i].name + "</td><td>" + data.columns[i].type + "</td></tr>";
				}
				dtl += "</table>";
				if ($scope.$$phase)
					$scope.data.tableDetails[tblName] = dtl;
				else {
					$scope.$apply(function () {
						$scope.data.tableDetails[tblName] = dtl;
					});
				}
			});
		}

		// Sort the tables
		$scope.data.tableNames = [];
		for (var tblName in kahuna.meta.allTables) {
			if ( ! kahuna.meta.allTables.hasOwnProperty(tblName))
			continue;
			$scope.data.tableNames.push(tblName);
		}
		$scope.data.tableNames.sort();

		// Watch tables accordion
		$scope.$watch(function () {
			var openFlags = '';
			for (var i = 0; i < $scope.data.tableNames.length; i++) {
				openFlags += $scope.data.tableAccordion[i] ? 'Y' : 'N';
			}
			return openFlags;
		}, function (idx) {
			console.log('Watch idx: ' + idx);
			var numOpen = 0;
			for (var i = 0; i < idx.length; i++) {
				if (idx.charAt(i) == 'Y') {
					console.log("Table " + $scope.data.tableNames[i] + " is open: " + $scope.data.tableAccordion[i]);
					numOpen++;
					showTableDetails(i);
				}
			}
			if ( ! numOpen)
				console.log("No table is open");
		}, true);

		function showResourceDetails(idx) {
			var res = $scope.data.topResources[idx];
			if ( ! res.accordionOpen)
				return;
			if ($scope.data.resourceDetails[idx])
				return;

			var resIdent = res.ident;
			KahunaData.query('AllResources', {filter: 'ident=' + resIdent}, function (data) {
				var resource = data[0];
				$scope.data.resourceDetails[resource.ident] = resource;
				$scope.data.debugMsg = resource;
			});
		}

		KahunaData.query('admin:apiversions', {filter: 'project_ident=' + $scope.currentProject.ident, order: 'ident desc'}, function (data) {
			$scope.data.newestApiVersion = null;
			if (data.length == 0)
				return;
			$scope.data.apiVersions = data;
//			kahuna.setInScope($scope, 'apiVersions', data);
			$scope.data.selectedApiVersion = data[0];

			KahunaData.query('AllTopResources', {pagesize: 100, filter: 'apiversion_ident=' + $scope.data.selectedApiVersion.ident,
						order: 'name'}, function (topResourcesData) {
				$scope.data.topResources = topResourcesData;
				var resourceNames = [];
//				for (var tblName in kahuna.meta.allTables) {
//					if ( ! kahuna.meta.allTables.hasOwnProperty(tblName))
//						continue;
//					resourceNames.push(tblName);
//				}
				for (var i = 0; i < topResourcesData.length; i++)
					resourceNames.push(topResourcesData[i].name);
				//resourceNames.sort();
				$scope.data.resourceNames = resourceNames;
				if (resourceNames.length > 0)
					$scope.data.selectedResourceName = resourceNames[0];
				else
					$scope.data.selectedResourceName = "MyResource";

				// Set a watch for the accordions
				$scope.$watch(function () {
					var openFlags = '';
					for (var i = 0; i < topResourcesData.length; i++) {
						openFlags += topResourcesData[i].accordionOpen ? 'Y' : 'N';
					}
					return openFlags;
				}, function (idx) {
					console.log('Watch idx: ' + idx);
					var numOpen = 0;
					for (var i = 0; i < idx.length; i++) {
						if (idx.charAt(i) == 'Y') {
							console.log("Resource " + $scope.data.topResources[i].name + " is open: " + $scope.data.topResources[i].accordionOpen);
							numOpen++;
							showResourceDetails(i);
						}
					}
					if ( ! numOpen)
						console.log("No resource is open");
				}, true);
			});
		});

		$scope.getJsonForTable = function (tbl) {
			if ($scope.data.tableJson[tbl.name])
				return;
			kahuna.meta.getTableDetails(tbl.name, function (data) {
				var apiVersionName = "?";
				if ($scope.data.selectedApiVersion)
					apiVersionName = $scope.data.selectedApiVersion.name;
				var json = "{\n";
				json += "    \"@metadata\": {\n";
				json += "        \"href\": \"" + $scope.data.serverUrl + $scope.currentAccount.url_name + "/" + 
						$scope.currentProject.url_name + "/" + apiVersionName + "/" + tbl.name + "\"\n";
				json += "        \"checksum\": \"abcdef0123456789\"\n";
				json += "    }";
				//for (var i = 0; i < )
				json += "\n}\n";
				$scope.data.tableJson[tbl.name] = json;
			});
		};

		KahunaData.query('AllApiKeys', {
					pagesize: 1000, 
					filter: 'project_ident=' + $scope.currentProject.ident + ' AND status=\'A\' AND origin is null',
					order: 'name'}, 
				function (data) {
			$scope.data.apiKeys = data;
			if (data.length > 0)
				$scope.data.selectedApiKey = data[0];
		});

		$scope.refreshDebug = function () {
			$scope.debugMsg = "$scope.data.resources[1].accordionOpen=" + $scope.data.resources[1].accordionOpen;
		};

		$scope.viewDocs = function () {
			var url = location.href;
			if (url.indexOf('#') > 0)
				url = url.substring(0, url.indexOf('#'));
			if (url.indexOf('?') > 0)
				url = url.substring(0, url.indexOf('?'));
			if (url.charAt(url.length - 1) != '/')
				url += '/';
			if (url.substring(0, 22) == ("http://localhost:8080/")) {
				url = "http://localhost:8080/LogicDesigner/";
			}
			var srcUrl = url + "../rest/" + kahuna.globals.currentAccount.url_name + "/" + 
				$rootScope.currentProject.url_name + "/" + $scope.data.selectedApiVersion.name + "/%40docs";
			if (url.substring(0, 22) == ("http://localhost:8080/")) {
				srcUrl = "http://localhost:8080/KahunaService/rest/" + kahuna.globals.currentAccount.url_name + "/" + 
				$rootScope.currentProject.url_name + "/" + $scope.data.selectedApiVersion.name + "/%40docs";
			}
//			window.open(url + 'swagger-ui?url=' + srcUrl + "&key=" + $scope.data.selectedApiKey.apikey, '_blank');
			$('#docsView').attr('src', url + 'swagger-ui?url=' + srcUrl + "&key=" + $scope.data.selectedApiKey.apikey);
		};
	}
};

